import React, { useEffect, useState } from "react";
import ComputerItem from "./ComputerItem";
import { listComputers } from "./ComputerServer";
const ComputerList = () => {
  const [computer, setComputer] = useState([]);
  const [search,setSearch] = useState([]);
  const ListListComputers = async () => {
    try {
      const res = await listComputers();
      const data = await res.json();
      setComputer(data.Computers);
    } catch (error) {
      console.log(error);
    }
  };
  useEffect(() => {
    ListListComputers();
  }, []);

  const handleChange = (e) =>{
    console.log("Busqueda:" + e.target.value)
  }

  return (
    <div>
      <div className="containerInput">
          <input className="form-control inputSearch" placeholder="..." value={search}/>
          <button className="btn btn-success"  onChange={handleChange}>Search</button>
      </div>
      <table className="table table-dark">
        <thead>
          <tr>
            <th scope="col">ID</th>
            <th scope="col">Name</th>
            <th scope="col">id_Computer</th>
            <th scope="col">id_Monitor</th>
            <th scope="col">Anydesk</th>
            <th scope="col">Switch</th>
            <th scope="col">Puert</th>
            <th scope="col">IP</th>
            <th scope="col">#</th>
            <th scope="col">#</th>
          </tr>
        </thead>
        {computer.map((computer) => {
          return (
            <ComputerItem
              key={computer.id}
              computer={computer}
              ListListComputers={ListListComputers}
            />
          );
        })}
      </table>
    </div>
  );
};

export default ComputerList;
